export const BondSelectionGuidePage: React.FC = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [completedSteps, setCompletedSteps] = useState<Set<number>>(new Set());
  const [isContentDrawerOpen, setIsContentDrawerOpen] = useState(false);

  const handleStepComplete = () => {
    setCompletedSteps((prev) => new Set([...prev, currentStep]));
    if (currentStep < courseStepsData.length) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handleStepBack = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleStepClick = (stepId: number) => {
    // –†–∞–∑—Ä–µ—à–∞–µ–º –ø–µ—Ä–µ—Ö–æ–¥ –∫ –ª—é–±–æ–º—É —à–∞–≥—É
    setCurrentStep(stepId);
    // –ó–∞–∫—Ä—ã–≤–∞–µ–º Drawer –ø–æ—Å–ª–µ –≤—ã–±–æ—Ä–∞ —à–∞–≥–∞
    setIsContentDrawerOpen(false);
  };

  const handleContentButtonClick = () => {
    setIsContentDrawerOpen(true);
  };

  const currentStepData = courseStepsData.find((s) => s.id === currentStep);

  return (
    <Box sx={{ width: '100%' }}>
      <Box sx={{ px: 3, py: 3, maxWidth: 1400, mx: 'auto' }}>
        {/* Header Card */}
        <Paper elevation={2} sx={{ p: 3, mb: 4 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', gap: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <SchoolIcon sx={{ fontSize: 40, color: 'primary.main' }} />
              <Box>
                <Typography variant="h4" component="h1" fontWeight={700}>
                  –°–æ–≤–µ—Ç—ã –ø–æ –≤—ã–±–æ—Ä—É –æ–±–ª–∏–≥–∞—Ü–∏–π
                </Typography>
                <Typography variant="body1" color="text.secondary" sx={{ mt: 0.5 }}>
                  –ü–æ—à–∞–≥–æ–≤—ã–µ —Å–æ–≤–µ—Ç—ã –ø–æ –æ—Ç–±–æ—Ä—É –æ–±–ª–∏–≥–∞—Ü–∏–π –¥–ª—è –ø–æ—Ä—Ç—Ñ–µ–ª—è
                </Typography>
              </Box>
            </Box>
            <Button variant="outlined" color="primary" onClick={handleContentButtonClick}>
              –°–û–î–ï–†–ñ–ê–ù–ò–ï
            </Button>
          </Box>
        </Paper>

      {/* Content Drawer */}
      <Drawer
        anchor="left"
        open={isContentDrawerOpen}
        onClose={() => setIsContentDrawerOpen(false)}
        PaperProps={{
          sx: {
            width: { xs: '100%', sm: 400 },
            maxWidth: '90vw',
          },
        }}
      >
        <Box sx={{ p: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between', borderBottom: 1, borderColor: 'divider' }}>
          <Typography variant="h6" fontWeight={600}>
            –°–æ–¥–µ—Ä–∂–∞–Ω–∏–µ
          </Typography>
          <IconButton onClick={() => setIsContentDrawerOpen(false)} size="small">
            <CloseIcon />
          </IconButton>
        </Box>
        <Box sx={{ p: 2, overflowY: 'auto', height: '100%' }}>
          <Stepper activeStep={-1} orientation="vertical" sx={{ mt: 2 }}>
            {courseStepsData.map((step) => {
              const isCompleted = completedSteps.has(step.id);
              const isCurrent = currentStep === step.id;

              return (
                <Step
                  key={step.id}
                  completed={isCompleted}
                  sx={{
                    cursor: 'pointer',
                    '&:hover': {
                      opacity: 0.8,
                    },
                  }}
                  onClick={() => handleStepClick(step.id)}
                >
                  <StepLabel
                    StepIconComponent={
                      isCompleted
                        ? () => <CheckCircleIcon sx={{ color: 'success.main' }} />
                        : undefined
                    }
                    sx={{
                      '& .MuiStepLabel-label': {
                        fontWeight: isCurrent ? 600 : 400,
                        color: isCurrent ? 'primary.main' : 'inherit',
                      },
                    }}
                  >
                    <Typography 
                      variant="body1" 
                      sx={{ 
                        whiteSpace: 'normal',
                        lineHeight: 1.5,
                      }}
                    >
                      –®–∞–≥ {step.id}: {step.title}
                    </Typography>
                  </StepLabel>
                </Step>
              );
            })}
          </Stepper>
        </Box>
      </Drawer>

        {/* Main Content */}
        <Fade in timeout={300}>
          <Paper elevation={2} sx={{ p: 4, overflowAnchor: 'none' }}>
            {currentStepData ? (
              <StepContent
                stepNumber={currentStepData.id}
                title={currentStepData.title}
                description={currentStepData.description}
                instruction={currentStepData.instruction}
                warning={currentStepData.warning}
                tip={currentStepData.tip}
                whatToDo={currentStepData.whatToDo}
                methodology={currentStepData.methodology}
                practicalApplication={currentStepData.practicalApplication}
                onStepComplete={handleStepComplete}
                onStepBack={handleStepBack}
              />
            ) : (
              <Box sx={{ textAlign: 'center', py: 4 }}>
                <Typography variant="h6" color="error">
                  –û—à–∏–±–∫–∞: —à–∞–≥ {currentStep} –Ω–µ –Ω–∞–π–¥–µ–Ω
                </Typography>
              </Box>
            )}

            {/* Course Completion */}
            {completedSteps.size === courseStepsData.length && (
              <Paper
                elevation={3}
                sx={{
                  p: 4,
                  mt: 4,
                  textAlign: 'center',
                  bgcolor: 'rgba(46, 125, 50, 0.1)',
                  border: 2,
                  borderColor: 'success.main',
                  borderRadius: 2,
                }}
              >
                <CheckCircleIcon sx={{ fontSize: 64, color: 'success.main', mb: 2 }} />
                <Typography variant="h4" gutterBottom fontWeight={700} color="success.dark">
                  –ü–æ–∑–¥—Ä–∞–≤–ª—è–µ–º!
                </Typography>
                <Typography variant="h6" color="text.secondary" gutterBottom>
                  –í—ã —É—Å–ø–µ—à–Ω–æ –∏–∑—É—á–∏–ª–∏ —Å–æ–≤–µ—Ç—ã –ø–æ –≤—ã–±–æ—Ä—É –æ–±–ª–∏–≥–∞—Ü–∏–π
                </Typography>
                <Typography variant="body1" color="text.secondary" sx={{ mt: 2 }}>
                  –¢–µ–ø–µ—Ä—å –≤—ã –∑–Ω–∞–µ—Ç–µ, –∫–∞–∫ –ø—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω–æ –ø–æ–¥—Ö–æ–¥–∏—Ç—å –∫ –æ—Ç–±–æ—Ä—É –æ–±–ª–∏–≥–∞—Ü–∏–π –¥–ª—è
                  –ø–æ—Ä—Ç—Ñ–µ–ª—è. –ü—Ä–∏–º–µ–Ω—è–π—Ç–µ —ç—Ç–∏ –∑–Ω–∞–Ω–∏—è –Ω–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ –∏ –ø—Ä–æ–¥–æ–ª–∂–∞–π—Ç–µ —Ä–∞–∑–≤–∏–≤–∞—Ç—å —Å–≤–æ–∏ –Ω–∞–≤—ã–∫–∏.
                </Typography>
              </Paper>
            )}
          </Paper>
        </Fade>
      </Box>
    </Box>
  );
};

export default BondSelectionGuidePage;
